package com.example;import org.mybatis.spring.annotation.MapperScan;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.builder.SpringApplicationBuilder;import org.springframework.boot.web.support.SpringBootServletInitializer;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;@MapperScan("com.example.dao")@ComponentScan@SpringBootApplicationpublic class DemoApplication extends SpringBootServletInitializer{    protected SpringApplicationBuilder configure(SpringApplicationBuilder builder) {        return builder.sources(DemoApplication.class);    }    public static void main(String[] args) {        SpringApplication.run(DemoApplication.class, args);    }}